import React, { useEffect, useState } from "react";
import { stringify } from 'query-string';

import { makeStyles } from "@material-ui/core/styles";

import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Table from "components/Table/Table.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";
import Search from "@material-ui/icons/Search";
import CustomInput from "components/CustomInput/CustomInput.js";
import Button from "components/CustomButtons/Button.js";

import Paginator from 'react-hooks-paginator';

const styles = {
  cardCategoryWhite: {
    "&,& a,& a:hover,& a:focus": {
      color: "rgba(255,255,255,.62)",
      margin: "0",
      fontSize: "14px",
      marginTop: "0",
      marginBottom: "0"
    },
    "& a,& a:hover,& a:focus": {
      color: "#FFFFFF"
    }
  },
  cardTitleWhite: {
    color: "#FFFFFF",
    marginTop: "0px",
    minHeight: "auto",
    fontWeight: "300",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    marginBottom: "3px",
    textDecoration: "none",
    "& small": {
      color: "#777",
      fontSize: "65%",
      fontWeight: "400",
      lineHeight: "1"
    }
  }
};

const useStyles = makeStyles(styles);

export default function ArtistList() {
  const classes = useStyles();

  const pageLimit = 3;
  const [offset, setOffset] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const [data, setData] = useState([]);
  const [currentData, setCurrentData] = useState([]);

  const [input, setInput] = useState({
    search: ''
  });

  // https://itunes.apple.com/lookup?id=20044&entity=song

  const search = () => {
    const query = {
      country: 'US',
      media: 'music',
      entity: 'musicArtist',
      term: `${input.search}`
    };      
    const apiUrl = 'https://itunes.apple.com/search';
    const url = `${apiUrl}?${stringify(query)}`;

    fetch(
      url,
      {method: "GET"}
    )
    .then(res => res.json())
    .then(response => {
      const artists = response.results.map(
        a => [
          a.artistId, 
          <a href={"albums?artist=" + a.artistId}>{a.artistName}</a>, 
          a.primaryGenreName
        ]
      )
      setData(artists);
    })
    .catch(error => console.log(error));
  }

  useEffect(() => {    
    search();
  }, []);

  useEffect(() => {
    setCurrentData(data.slice(offset, offset + pageLimit));
  }, [offset, data]);

  const handleInputChange = (e) => setInput({
    ...input,
    [e.currentTarget.name]: e.currentTarget.value
  })  

  return (
    <div>
      <div className={classes.searchWrapper}>
        <CustomInput                    
          formControlProps={{
            className: classes.margin + " " + classes.search
          }}
          inputProps={{
            placeholder: "Search",
            name: "search",
            inputProps: {
              "aria-label": "Search",
              "value": input.search,
              "onChange": handleInputChange
            }
          }}
        />
        <Button onClick={search} color="white" aria-label="edit" justIcon round>
          <Search />
        </Button>
      </div>

      <GridContainer>
        <GridItem xs={12} sm={12} md={12}>
          <Card>
            <CardHeader color="primary">
              <h4 className={classes.cardTitleWhite}>Simple Table</h4>
              <p className={classes.cardCategoryWhite}>
                Here is a subtitle for this table
            </p>
            </CardHeader>
            <CardBody>
              <Table
                tableHeaderColor="primary"
                tableHead={["ID", "Name", "Genre"]}
                tableData={currentData}
              />
            </CardBody>
          </Card>
        </GridItem>
      </GridContainer>

      <Paginator
        totalRecords={data.length}
        pageLimit={pageLimit}
        pageNeighbours={2}
        setOffset={setOffset}
        currentPage={currentPage}
        setCurrentPage={setCurrentPage}
      />
    </div>
  );
}
